package task2;

public class AverageOfThree {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=25;
		int num2=45;
		int num3=65;
		System.out.println(avergaeOfThree(num1,num2,num3));

	}

	private static double avergaeOfThree(int num1, int num2, int num3) {
		// TODO Auto-generated method stub
		return(num1+num2+num3)/3;
		
	}

}
